script:
function handle_filter(arg1, arg2, arg3) {
    // console.log(arg1, arg2, arg3);
    frappe.call({
        method: "suvaidyam.services.apis.campaign_get",
        args: {
            param1: arg1,
            param2: arg2,
            param3: arg3
        },
        callback: function(r) {
            // console.log("API Response:", r);
            if (r.message) {
                let data = r.message;
                // console.log("campaign data:", data);
                handleData(data);
            }
        }
    });
}

//  filter pop up
let d = new frappe.ui.Dialog({
    title: 'Make a call',
    fields: [
        {
            label: 'State',
            fieldname: 'state',
            fieldtype: 'Link',
            options: 'State',
            only_select: 1
        },
        {
            label: 'Centre',
            fieldname: 'centre',
            fieldtype: 'Link',
            options: 'Centre',
            only_select: 1,
            "get_query": function () {
                var state = cur_dialog.fields_dict.state.value;
                return {
                    filters: {
                        'state': state
                    }
                };
            }
        },
        {
            label: 'Agent',
            fieldname: 'agent',
            fieldtype: 'Link',
            options: 'SVA User',
            only_select: 1,
            "get_query": function () {
                var centre = cur_dialog.fields_dict.centre.value;
                return {
                    filters: {
                        'custom_centre': centre,
                        'role_profile': 'Agent',
                    }
                };
            }
        },
    ],
    size: 'small',
    secondary_action_label: 'Cancel',
    primary_action_label: 'Apply Filter',
    primary_action(values) {
        if (values) {
            console.log("values", values);
            handle_filter(values.state, values.centre, values.agent);
            d.hide();
        } else {
            frappe.show_alert({ message: "Please select all fields", indicator: "yellow" });
        }
    },
    secondary_action() {
        d.hide();
    },

});

function OpenPopUp(){
        d.show();
        // console.log('kkk')
    }
    root_element.querySelector('.filter_dash').addEventListener('click', OpenPopUp);


/// API call to get campaign data
 frappe.call({
        method: "suvaidyam.services.apis.campaign_get",
        callback: function(r) {
            if (r.message) {
                var data = r.message;
                // console.log("campaign:", data);
                handleData(data);
            }
        }
    });

