                    </div>
                </div>
            </div>
        `;
    });

    campaignDataDiv.innerHTML = cardHtml;
}

//  filter clear
root_element.getElementById('clear_filter').addEventListener('click', function() {
    d.fields_dict.state.set_value('');
    d.fields_dict.centre.set_value('');
    d.fields_dict.agent.set_value('');

    storedData = [];
    console.log('Data cleared', storedData);
    campaignDataDiv.innerHTML = '';

    frappe.call({
        method: "suvaidyam.services.apis.campaign_get",
        callback: function(r) {
            if (r.message) {
                var data = r.message;
                // console.log("campaign:", data);
                handleData(data);
            }
        }
    });
});
